const newYear="1 Jan 2022",daysEl=document.getElementById("days"),hoursEl=document.getElementById("hours"),minsEl=document.getElementById("mins"),secondsEl=document.getElementById("seconds"),countdown=()=>{const e=(new Date(newYear)-new Date)/1e3,n=Math.floor(e/3600/24),t=Math.floor(e/3600)%24,o=Math.floor(e/60)%60,a=Math.floor(e%60);daysEl.innerHTML=formatDate(n),hoursEl.innerHTML=formatDate(t),minsEl.innerHTML=formatDate(o),secondsEl.innerHTML=formatDate(a),console.log(a)},formatDate=e=>e<10?`0${e}`:e;countdown(),setInterval(countdown,1e3);